import React, { useState, useEffect } from 'react';
import './style.css';
import { useNavigate, useLocation } from 'react-router-dom';

function Header() {
  const navigate = useNavigate();
  const location = useLocation();
  
  const [searchQuery, setSearchQuery] = useState("");
  const [songs, setSongs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [error, setError] = useState("");

  // Читаем параметры из URL
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const query = params.get("query") || "";
    const page = parseInt(params.get("page"), 10) || 1;

    setSearchQuery(query);
    setCurrentPage(page);
  }, [location]);

  // Функция для отправки запросов с Fetch API
  const fetchSongs = async () => {
    try {
      const response = await fetch(`http://localhost:5000/?page=${currentPage}&search=${searchQuery}`);
      if (!response.ok) {
        throw new Error('Ошибка загрузки данных');
      }
      const data = await response.json();
      setSongs(data.songs);
      setTotalPages(data.totalPages);
    } catch (error) {
      setError(error.message);
    }
  };

  useEffect(() => {
    if (searchQuery || currentPage) {
      fetchSongs();
    }
  }, [searchQuery, currentPage]);

  // Обработчик изменения поискового запроса
  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
    setCurrentPage(1);
    navigate(`/?query=${event.target.value}&page=1`); // Обновляем URL с новым запросом
  };

  // Обработчик перехода на предыдущую страницу
  const goBack = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
    navigate(`/?query=${searchQuery}&page=${Math.max(currentPage - 1, 1)}`);
  };

  return (
    <div className="Header">
      <div style={{ display: "flex" }}>
        <img src="image.png" style={{ height: "37px", marginTop: "15px" }} alt="Logo" />
        <p style={{ color: "white", fontSize: "20px", marginLeft: "10px" }}>Promote your music</p>
      </div>
      <div style={{ display: "flex" }}>
        <button 
          onClick={goBack} 
          style={{
            marginTop: "20px",
            marginRight: "10px",
            height: "30px",
            width: "80px",
            borderRadius: "20px",
            cursor: "pointer",
            backgroundColor: "white",
            color: "black",
            border: "none"
          }}
        >
          Go back
        </button>
        <input 
          type="text" 
          placeholder="Search for a song" 
          value={searchQuery}
          onChange={handleSearch} 
          style={{
            width: "243px", 
            height: "33px", 
            marginTop: "15px", 
            marginRight: "20px"
          }} 
        />
      </div>

      {/* Выводим ошибки, если они есть */}
      {error && <div style={{ color: 'red' }}>{error}</div>}

      {/* Выводим песни */}
      <div>
        {songs.map((song, index) => (
          <div key={index}>
            <h3>{song.title}</h3>
            <p>{song.album}</p>
          </div>
        ))}
      </div>

      {/* Выводим страницу */}
      <div>
        <span>Page {currentPage} of {totalPages}</span>
      </div>
    </div>
  );
}

export default Header;
