import './style.css';
import React from 'react';
import { useState} from 'react';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Header() {
    const navigate = useNavigate();
    const [searchQuery, setSearchQuery] = useState(""); // Для хранения поискового запроса
    const [songs, setSongs] = useState([]); // Для хранения списка песен
    const [currentPage, setCurrentPage] = useState(1); // Для текущей страницы
    const [totalPages, setTotalPages] = useState(1); // Для количества страниц
    const [error, setError] = useState(""); // Для ошибок, если что-то пойдет не так

    // Функция для отправки запросов с Fetch API
    const fetchSongs = async () => {
        try {
            // Отправляем запрос с поисковым запросом и пагинацией
            const response = await fetch(`http://localhost:5000/?page=${currentPage}&search=${search}`);
            
            if (!response.ok) {
                throw new Error('Ошибка загрузки данных');
            }

            // Обрабатываем данные в формате JSON
            const data = await response.json();
            setSongs(data.songs); // Обновляем состояние с песнями
            setTotalPages(data.totalPages); // Обновляем количество страниц
        } catch (error) {
            setError(error.message); // Устанавливаем ошибку, если что-то пошло не так
        }
    };

    useEffect(() => {
        fetchSongs(); // Запрашиваем песни при изменении searchQuery или currentPage
    }, [searchQuery, currentPage]);

    useEffect(() => {
        // Обновляем URL, когда изменяется searchQuery или currentPage
        navigate(`/search?query=${searchQuery}&page=${currentPage}`);
    }, [searchQuery, currentPage, navigate]);

    const goBack = () => {
        setCurrentPage((prevPage) => Math.max(prevPage - 1, 1)); // Переход на предыдущую страницу данных
    };

    return (
        <div className="Header">
            <div style={{ display: "flex" }}>
                <img src="image.png" style={{ height: "37px", marginTop: "15px" }} alt="Logo" />
                <p style={{ color: "white", fontSize: "20px", marginLeft: "10px" }}>Promote your music</p>
            </div>
            <div style={{ display: "flex" }}>
                <button 
                    onClick={goBack} // Переход на предыдущую страницу данных
                    style={{
                        marginTop: "20px",
                        marginRight: "10px",
                        height: "30px",
                        width: "80px",
                        borderRadius: "20px",
                        cursor: "pointer",
                        backgroundColor: "white",
                        color: "black",
                        border: "none"
                    }}
                >
                    Go back
                </button>
                <input 
                    type="text" 
                    placeholder="Search for a song" 
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)} // Обновляем состояние поиска
                    style={{
                        width: "243px", 
                        height: "33px", 
                        marginTop: "15px", 
                        marginRight: "20px"
                    }} 
                />
            </div>

            {/* Выводим ошибки, если они есть */}
            {error && <div style={{ color: 'red' }}>{error}</div>}

            {/* Выводим песни */}
            <div>
                {songs.map((song, id) => (
                    <div key={id}>
                        <h3>{song.title}</h3>
                        <p>{song.album}</p>
                    </div>
                ))}
            </div>

            {/* Пагинация */}
            <div>
                <span>Page {currentPage} of {totalPages}</span>
            </div>
        </div>
    );
}

export default Header;
