import './style.css';
import image from './image.png';
import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import debounce from 'lodash/debounce';

function Header() {
    const navigate = useNavigate();
    const [searchQuery, setSearchQuery] = useState("");
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    // Делаем debounce для функции поиска
    const debouncedSearch = useCallback(
        debounce(async (query) => {
            if (!query.trim()) {
                setError("Введите корректный запрос");
                return;
            }

            try {
                setLoading(true);
                const response = await fetch(`http://localhost:5000/songs?search=${query}`);
                if (!response.ok) {
                    throw new Error("Ошибка поиска песни");
                }

                const data = await response.json();
                if (data.songs.length > 0) {
                    const song = data.songs[0];
                    navigate(`/song/${song.id}`);
                } else {
                    setError("Песня не найдена");
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        }, 300), [] // useCallback и debounce комбинируются для оптимизации
    );

    const handleInputChange = (e) => {
        const query = e.target.value;
        setSearchQuery(query);
        setError("");
        debouncedSearch(query); // Используем debounced функцию
    };

    return (
        <div className="Header">
            <div style={{ display: "flex" }}>
                <img src={image} style={{ height: "37px", marginTop: "15px" }} alt="Logo" />
                <p style={{ color: "white", fontSize: "20px", marginLeft: "10px" }}>Promote your music</p>
            </div>
            <div style={{ display: "flex" }}>
                <button
                    onClick={() => navigate(-1)}
                    style={{
                        marginTop: "20px",
                        marginRight: "10px",
                        height: "30px",
                        width: "80px",
                        borderRadius: "20px",
                        cursor: "pointer",
                        backgroundColor: "white",
                        color: "black",
                        border: "none"
                    }}
                >
                    Go back
                </button>
                <input
                    type="text"
                    placeholder="Search for a song"
                    value={searchQuery}
                    onChange={handleInputChange}
                    style={{
                        width: "243px",
                        height: "33px",
                        marginTop: "15px",
                        marginRight: "20px"
                    }}
                />
            </div>
            {loading && <div style={{ color: 'blue' }}>Загрузка...</div>}
            {error && <div style={{ color: 'red' }}>{error}</div>}
        </div>
    );
}

export default Header;
