import './style.css';
import image from './image.png';
import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import debounce from 'lodash/debounce';

function Header() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Словарь для поиска по названию песни и маршруту
  const songRoutes = {
    "12 Notes": "/tw_notes_song",
    "Ways To Go": "/ways_to_go_song",
    "In A Little": "/in_a_little_song",
    "Different Kind Of Beautiful": "/different_kind_of_beautiful_song",
    "Pick Me": "/pick_me_song",
    "Sacrifice Tomorrow": "/sacrifice_tomorrow_song",
    "I Sent My Therapist To Therapy": "/i_sent_my_therapist_to_therapy_song",
    "The Arsonist": "/the_arsonist_song",
    "Lead Me To Water": "/lead_me_to_water_song",
    "By Now": "/by_now_song",
    "King Size Bed": "/king_size_bed_song",
    "Love The Ones Who Leave": "/love_the_ones_who_leave_song",
    "Mind Is A Prison": "/mind_is_a_prison_song",
    "Demons": "/demons_song",
    "Oh My God": "/oh_my_god_song",
    "The Book of You & I": "/the_book_of_you_&_i_song",
    "Match In The Rain": "/match_in_the_rain_song",
    "Jesus In LA": "/jesus_in_la_song",
    "Im Not A Cynic": "/im_not_a_cynic_song",
    "Alamo": "/alamo_song",
    "Must Have Been The Wind": "/must_have_been_the_wind_song",
    "Just Like You": "/just_like_you_song"
  };

  // Функция для дебаунсинга и поиска песни
  const debouncedSearch = useCallback(
    debounce(async (query) => {
      if (!query.trim()) {
        setError("Введите корректный запрос");
        return;
      }

      setLoading(true);
      try {
        const song = Object.keys(songRoutes).find((title) =>
          title.toLowerCase().includes(query.toLowerCase())
        );

        if (song) {
          const route = songRoutes[song];
          if (route) {
            navigate(route);
          } else {
            setError("Песня не найдена");
          }
        } else {
          setError("Песня не найдена");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    }, 300),
    []
  );

  const handleInputChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    setError("");
    debouncedSearch(query); // Вызываем дебаунс функцию
  };

  return (
    <div className="Header">
      <div style={{ display: "flex" }}>
        <img src={image} style={{ height: "37px", marginTop: "15px" }} alt="Logo" />
        <p style={{ color: "white", fontSize: "20px", marginLeft: "10px" }}>Promote your music</p>
      </div>
      <div style={{ display: "flex" }}>
        <button
          onClick={() => navigate(-1)}
          style={{
            marginTop: "20px",
            marginRight: "10px",
            height: "30px",
            width: "80px",
            borderRadius: "20px",
            cursor: "pointer",
            backgroundColor: "white",
            color: "black",
            border: "none"
          }}
        >
          Go back
        </button>
        <input
          type="text"
          placeholder="Search for a song"
          value={searchQuery}
          onChange={handleInputChange}
          style={{
            width: "243px",
            height: "33px",
            marginTop: "15px",
            marginRight: "20px"
          }}
        />
      </div>
      {loading && <div style={{ color: 'blue' }}>Загрузка...</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
    </div>
  );
}

export default Header;
